// The translate global attribute is an enumerated attribute that is used to
// specify whether an element's attribute values and the values of its Text node
// children are to be translated when the page is localized, or whether to leave
// them unchanged.
func (e *BDIElement) TRANSLATE(c BdiTranslateChoice) *BDIElement {
	if e.StringAttributes == nil {
		e.StringAttributes = treemap.New[string, string]()
	}
	e.StringAttributes.Set("translate", string(c))
	return e
}

type BdiTranslateChoice string

const (
	// indicates that the element should be translated when the page is localized.
	BdiTranslate_empty BdiTranslateChoice = ""
	// indicates that the element should be translated when the page is localized.
	BdiTranslate_yes BdiTranslateChoice = "yes"
	// indicates that the element must not be translated when the page is localized.
	BdiTranslate_no BdiTranslateChoice = "no"
)

// Remove the attribute TRANSLATE from the element.
func (e *BDIElement) TRANSLATERemove(c BdiTranslateChoice) *BDIElement {
	if e.StringAttributes == nil {
		return e
	}
	e.StringAttributes.Del("translate")
	return e
}

// Merges the singleton store with the given object

func (e *BDIElement) DATASTAR_STORE(v any) *BDIElement {
	if e.CustomDataAttributes == nil {
		e.CustomDataAttributes = treemap.New[string, string]()
	}
	b, err := json.Marshal(v)
	if err != nil {
		panic(err)
	}
	e.CustomDataAttributes.Set("store", html.EscapeString(string(b)))
	return e
}

